ExchangeService exchange = null;

public void ConnectEWS(string ewsURL, string _exchangeVersion, string username, string password)
{
	ServicePointManager.ServerCertificateValidationCallback = delegate { return true; }; 
	
	ExchangeVersion exVer = new ExchangeVersion();
		if (_exchangeVersion == "Exchange2013_SP1")
		{
			exVer = ExchangeVersion.Exchange2013_SP1;
		}
		else if (_exchangeVersion == "Exchange2013")
		{
			 exVer = ExchangeVersion.Exchange2013;
		}
		else if (_exchangeVersion == "Exchange2010_SP2")
		{
			 exVer = ExchangeVersion.Exchange2010_SP2;
		}
		else if (_exchangeVersion == "Exchange2010_SP1")
		{
			exVer = ExchangeVersion.Exchange2010_SP1;
		}
		else if (_exchangeVersion == "Exchange2010")
		{
			exVer = ExchangeVersion.Exchange2010;
		}
	try   
	{
		exchange = new ExchangeService(exVer);
		exchange.Url = new Uri(ewsURL);
		exchange.Credentials = new WebCredentials(username, password);
		
	}   
	catch 
	{  
		
	}
}



public FolderId GetFolderId(string folderName, FolderId parentFolderId)
{
	FolderId folderId = null;
	FolderView view = new FolderView(100);
	view.PropertySet = new PropertySet(BasePropertySet.IdOnly);
	view.PropertySet.Add(FolderSchema.DisplayName);
	view.Traversal = FolderTraversal.Shallow;
	FindFoldersResults findFolderResults = null;

	if (parentFolderId == null)
		findFolderResults = exchange.FindFolders(WellKnownFolderName.Inbox, view);
	else
		findFolderResults = exchange.FindFolders(parentFolderId, view);

	foreach (Folder f in findFolderResults)
	{
		if (f.DisplayName == folderName)
		{
			folderId = f.Id;
			return folderId;
		}
	}

	return folderId;
}

public DataTable GetMailsByFilter(DateTime Filter_StartDateTime, int Maximum_Row, string folderPath)
{
	FolderId destFolderId = null;
	if (!string.IsNullOrEmpty(folderPath) && folderPath != "Inbox")
	{
		FolderId currentFolderId = null;
		string[] folderNames = folderPath.Split('\\');
		foreach (var folderName in folderNames)
		{
			currentFolderId = GetFolderId(folderName, currentFolderId);
		}

		destFolderId = currentFolderId;
	}
	else
	{
		destFolderId = WellKnownFolderName.Inbox;
	}

	SearchFilter.IsGreaterThanOrEqualTo filter = new SearchFilter.IsGreaterThanOrEqualTo(ItemSchema.DateTimeReceived, Filter_StartDateTime);

	if (exchange != null)
	{
		PropertySet itempropertyset = new PropertySet(BasePropertySet.FirstClassProperties);
		itempropertyset.RequestedBodyType = BodyType.Text;
		ItemView itemview = new ItemView(Maximum_Row);
		itemview.PropertySet = itempropertyset;

		FindItemsResults<Item> findResults = exchange.FindItems(destFolderId, filter, itemview);
		DataTable dt = new DataTable();
		dt.Columns.Add("Id");
		dt.Columns.Add("Date");
		dt.Columns.Add("From Address");
		dt.Columns.Add("From Name");
		dt.Columns.Add("Subject");
		dt.Columns.Add("Body");
		dt.Columns.Add("HasAttachment");


		foreach (Item item in findResults)
		{
			EmailMessage message = EmailMessage.Bind(exchange, item.Id, itempropertyset);

			dt.Rows.Add(new string[]
			{
			message.Id.UniqueId, message.DateTimeReceived.ToString(), message.From.Address, message.From.Name, message.Subject, message.Body, ((message.HasAttachments) ? "Yes" : "No")
			});
		}
		return dt;
	}
	else
	{
		return null;
	}
}

public int DownloadAttachmentsByPattern(string msgid, string downloadFolder, string pattern)
{
	EmailMessage message = EmailMessage.Bind(exchange, new ItemId(msgid));
	int res=0;
	if (message.HasAttachments || !message.HasAttachments)
	{
		res = message.Attachments.Count;
		for(int i=0; i<message.Attachments.Count;i++)
		{
			FileAttachment fileAttachment = message.Attachments[i] as FileAttachment;
			if(fileAttachment.Name.ToLower().Contains(pattern.ToLower()))
			{
				Random rnd = new Random();
				int rndNum = rnd.Next(1, 100);
				fileAttachment.Load(@"" + downloadFolder + "\\" + rndNum + "_" + fileAttachment.Name);
			}
			else
			{
				res--;
			}
		}
	}
	return res;
}

public int DownloadAttachments(string msgid, string downloadFolder)
{
	EmailMessage message = EmailMessage.Bind(exchange, new ItemId(msgid));
	if (message.HasAttachments || !message.HasAttachments)
	{
		for(int i=0; i<message.Attachments.Count;i++)
		{
		FileAttachment fileAttachment = message.Attachments[i] as FileAttachment;
		Random rnd = new Random();
        int rndNum = rnd.Next(1, 100);
		fileAttachment.Load(@"" + downloadFolder + "\\" + rndNum + "_" + fileAttachment.Name);
		}
	}
	return message.Attachments.Count;
}

public void DownloadMail(string msgid, string downloadFolder)
{
		PropertySet psPropSet = new PropertySet(EmailMessageSchema.Subject,EmailMessageSchema.MimeContent);		
		EmailMessage emEmailMessage = EmailMessage.Bind(exchange, new ItemId(msgid), psPropSet);
		string mailFileName="";
		try
		{
			mailFileName = emEmailMessage.Subject.ToString().Trim();
			string rExp = @"[^\w\d]";
			mailFileName = Regex.Replace(mailFileName, rExp, "") + ".eml";
		}
		catch
		{
			mailFileName = "No-subject.eml";
		}
		
		FileStream fsFileStream = new FileStream(downloadFolder + "\\" + mailFileName, FileMode.Create);
		fsFileStream.Write(emEmailMessage.MimeContent.Content, 0, emEmailMessage.MimeContent.Content.Length);
		fsFileStream.Close();
}

public string CalculateMD5Hash(string input)
{   
    System.Security.Cryptography.MD5 md5 = System.Security.Cryptography.MD5.Create();

    byte[] inputBytes = System.Text.Encoding.ASCII.GetBytes(input);

    byte[] hash = md5.ComputeHash(inputBytes);

    System.Text.StringBuilder sb = new System.Text.StringBuilder();

    for (int i = 0; i < hash.Length; i++)
    {
        sb.Append(hash[i].ToString("X2"));
    }
    return sb.ToString();
}

public void CreateAppointment(string anotherAccountAddress,string subject,string body,string startDatetime, string endDateTime,
            int remainderAsMinute, string locationName, string locationMailAdress, string requiredAttendeesAsDelimitedComma, string optionalAttendeesAsDelimitedComma)
{
	Appointment appointment = new Appointment(exchange);
	Mailbox anotherMailbox=null;
	FolderId anotherCalendarFolder=null;

	//Using another mailbox operations.
	if (anotherAccountAddress != "")
	{
		anotherMailbox = new Mailbox(anotherAccountAddress);
		anotherCalendarFolder = new FolderId(WellKnownFolderName.Calendar, anotherMailbox);
	}

	//appointment settings.
	appointment.Subject = subject;
	appointment.Body = body;
	appointment.Start = Convert.ToDateTime(startDatetime).ToUniversalTime(); 
	appointment.End = Convert.ToDateTime(endDateTime).ToUniversalTime(); 
	appointment.ReminderMinutesBeforeStart = remainderAsMinute;
	
	//meeting room settings.
	if (locationMailAdress != "" && locationName != "")
	{
		appointment.Location = locationName;
		appointment.Resources.Add(locationName, locationMailAdress);
	}                       

	//Attendees settings.
	string[] requiredAttendees = requiredAttendeesAsDelimitedComma.Split(',');
	for (int i = 0; i < requiredAttendees.Length; i++)
	{
		if(requiredAttendees[i].ToString()!="")
		{
			appointment.RequiredAttendees.Add(requiredAttendees[i].ToString());
		}
	}
	string[] optioanlAttendees = optionalAttendeesAsDelimitedComma.Split(',');
	for (int i = 0; i < optioanlAttendees.Length; i++)
	{
		if(optioanlAttendees[i].ToString()!="")
		{
			appointment.OptionalAttendees.Add(optioanlAttendees[i].ToString());
		}
	}

	//save and send invitations to attendees.
	if (anotherAccountAddress != "")
	{
		appointment.Save(anotherCalendarFolder, SendInvitationsMode.SendToAllAndSaveCopy);
	}
	else
	{
		appointment.Save(SendInvitationsMode.SendToAllAndSaveCopy);
	}
}

public void DeleteMail(string msgid)
{
	EmailMessage message = EmailMessage.Bind(exchange, new ItemId(msgid));
	message.Delete(DeleteMode.SoftDelete);
}

public void ReplyMail(string msgid, bool replyToAll, string messageText, DataTable attachments)
{
	if (exchange != null)
	{

		EmailMessage message = EmailMessage.Bind(exchange, msgid, BasePropertySet.IdOnly);

		ResponseMessage responseMessage = message.CreateReply(replyToAll);

		responseMessage.BodyPrefix = messageText;
		EmailMessage reply = responseMessage.Save();
		for (int i = 0; i < attachments.Rows.Count; i++)
		{
			reply.Attachments.AddFileAttachment(attachments.Rows[i]["Path"].ToString());

		}
		reply.Update(ConflictResolutionMode.AutoResolve);
		reply.SendAndSaveCopy();
	}

}

public void MoveToFolder(string msgid, string destinationFolder)
{
	PropertySet propSet = new PropertySet(BasePropertySet.IdOnly, EmailMessageSchema.Subject, EmailMessageSchema.ParentFolderId);
	EmailMessage beforeMessage = EmailMessage.Bind(exchange, msgid, propSet);

	FolderView view = new FolderView(100);
	view.PropertySet = new PropertySet(BasePropertySet.IdOnly);
	view.PropertySet.Add(FolderSchema.DisplayName);
	view.Traversal = FolderTraversal.Deep;
	FindFoldersResults findFolderResults = exchange.FindFolders(WellKnownFolderName.Root, view);

	FolderId destFolderId = null;
	if (!string.IsNullOrEmpty(destinationFolder) && destinationFolder != "Inbox")
	{
		FolderId currentFolderId = null;
		string[] folderNames = destinationFolder.Split('\\');
		foreach (var folderName in folderNames)
		{
			currentFolderId = GetFolderId(folderName, currentFolderId);
		}

		destFolderId = currentFolderId;
	}
	else
	{
		destFolderId = WellKnownFolderName.Inbox;
	}

	Item item = beforeMessage.Move(destFolderId);
}

public void SendMail(string subject, string body, string toReceipents, string ccReceipents, string bccReceipents, DataTable attachments)
{
	if (exchange != null)
	{
		EmailMessage message = new EmailMessage(exchange);

		message.Subject = subject;
		message.Body = body;
		toReceipents = toReceipents.Replace(';',',');
		ccReceipents = ccReceipents.Replace(';',',');
		bccReceipents = bccReceipents.Replace(';',',');

		if (toReceipents != "")
		{
			string[] arr = toReceipents.Split(',');
			for (int i = 0; i < arr.Length; i++)
			{
				message.ToRecipients.Add(arr[i].ToString());
			}                    
		}

		if (ccReceipents != "")
		{
			 string[] arr = ccReceipents.Split(',');
			 for (int i = 0; i < arr.Length; i++)
			 {
				 message.CcRecipients.Add(arr[i].ToString());
			 }
		}
		if (bccReceipents != "")
		{
			string[] arr = bccReceipents.Split(',');
			for (int i = 0; i < arr.Length; i++)
			{
				message.BccRecipients.Add(arr[i].ToString());
			}
		}
		for (int i = 0; i < attachments.Rows.Count; i++)
		{
			message.Attachments.AddFileAttachment(attachments.Rows[i]["Path"].ToString());

		}
		message.SendAndSaveCopy();
	}
}
